INSERT INTO `emlog_blog` VALUES ('10', 'Yii2 引入twig 模板引擎', '1549728342', '1、composer require yii2-soft/yii2-twig<br />\r\n<br />\r\n2、项目配置文件中引入配置信息<br />\r\n<pre style=\"background-color:#FFFFFF;font-family:宋体;font-size:13.5pt;\">\r\n<p class=\"MsoNormal\">\r\n	\'components\' =&gt; [ &nbsp;<o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;... &nbsp;<o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp; \'view\' =&gt; <o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;[ &nbsp;\'class\' =&gt; \'yii\\web\\View\', &nbsp;<o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'renderers\' =&gt; [ &nbsp;<o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'twig\' =&gt; [ &nbsp;<o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'class\' =&gt; \'yii\\twig\\ViewRenderer\', &nbsp;<o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'cachePath\' =&gt; \'@runtime/Twig/cache\', &nbsp;<o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;// Array of twig options: &nbsp;\'options\' =&gt; [ &nbsp;\'auto_reload\' =&gt; true, &nbsp;], <o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \'globals\' =&gt; [\'html\' =&gt; \'\\yii\\helpers\\Html\'], <o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \'uses\' =&gt; [\'yii\\bootstrap\'], <o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ], &nbsp;<o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;], <o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ], <o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;... <o:p></o:p>\r\n\r\n\r\n</p>\r\n\r\n<p class=\"MsoNormal\">\r\n	&nbsp;],\r\n</p>\r\n</pre>', '', '', '1', '10', 'blog', '3', '0', '0', 'n', 'n', 'n', 'y', 'y', '', '', '');
